HttpResponse httpPostSoap(String destUrl, String postData, String user, String pwd) {
    HttpPost postRequest = new HttpPost(destUrl);

    Credentials credentials = new UsernamePasswordCredentials(user, pwd);
    CredentialsProvider credentialsProvider = new BasicCredentialsProvider();
    credentialsProvider.setCredentials(AuthScope.ANY, credentials);

    HttpClientBuilder builder = HttpClientBuilder.create();
    String proxyHost = System.getProperty("http.proxyHost");
    String proxyPort = System.getProperty("http.proxyPort");
    if (proxyHost != null && !proxyHost.equals(""))
        builder.setProxy(new HttpHost(proxyHost, (new Integer(proxyPort)).intValue()));
    CloseableHttpClient requestHttpClient = builder.setDefaultCredentialsProvider(credentialsProvider).build();

    HttpEntity entity = new ByteArrayEntity(postData.getBytes("UTF-8"), ContentType.TEXT_XML);
    postRequest.setEntity(entity);

    // Send SOAP request
    HttpResponse response = requestHttpClient.execute(postRequest);

    return response;
}
void checkHTTPResponseStatus(HttpResponse response){
    StatusLine status = response.getStatusLine();
    summaryMessage = summaryMessage + "HTTP status : " + status.getStatusCode() + " : " + status.getReasonPhrase()  + " : "
    odiRef.setSummaryMessage(summaryMessage)
    if (status.getStatusCode() != 200) {
        throw new OdiKMException ("HTTP request failed.");
    }
}
String getRequestStateFromResponse(String responseText) {

    String requestState = "";

    XMLInputFactory factory = XMLInputFactory.newInstance();
    XMLStreamReader rdr = factory.createXMLStreamReader(new StringReader(responseText));
    while (rdr.hasNext()) {
        if (rdr.next() == XMLStreamConstants.START_ELEMENT) {
            if (rdr.getLocalName().equals("state") ) {
                requestState = rdr.getElementText();
            }
        }
    }
    return requestState;
}
